import Person from './Person';
import Role from './Role';
import { PxpEntity } from '../PxpEntity';
export default class User extends PxpEntity {
    userId: number;
    username: string;
    password?: string;
    style?: string;
    expiration?: Date;
    authenticationType?: string;
    authenticationId?: string;
    hash?: string;
    salt?: string;
    person: Person;
    roles: Role[];
    personId: number;
    roleId: number;
    static getUis(userId: number): Promise<number[]>;
}
